mapper:
SELECT A.GSXX01,A.FYBM01 BM01,(SELECT FYBM02 FROM FYBM WHERE FYBM01 = A.FYBM01) BMMC, A.FYLX01 FYBM,(SELECT FYLX02 FROM FYLX WHERE FYLX01 = A.FYLX01) FYXM, A.KMDM,A.YSQJ, A.YSJE, A.FSJE, (A.YSJE - A.FSJE) SYJE FROM FYZXB A WHERE 1 =1
AND A.FYLX01 = #{FYXM}
AND A.FYBM01 = #{BMMC}
AND TO_DATE(A.YSQJ,'YYYY-MM') >= TO_DATE(#{YSQJ_S}, 'YYYY-MM')
AND TO_DATE(A.YSQJ,'YYYY-MM') <= TO_DATE(#{YSQJ_E},'YYYY-MM')
JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL
