SELECT A.BXRQ,A.BXRMC,B.FYBM01 BM01, (SELECT FYBM02 FROM FYBM WHERE FYBM01 = B.FYBM01) BMMC, B.FYLX01 FYBM,(SELECT FYLX02 FROM FYLX WHERE FYLX01 = B.FYLX01) FYXM, B.YSQJ, B.BXJE,B.BZ FROM FYBXD A, FYBXDITEM B WHERE A.GSXX01 = B.GSXX01 AND A.FYBXD01 = B.FYBXD01 AND A.SHRQ IS NOT NULL
AND B.FYBM01 = #{BM01}
AND B.FYLX01 = #{FYBM}
AND TO_DATE(A.YSQJ,'YYYY-MM') >= TO_DATE(#{YSQJ_S}, 'YYYY-MM')
AND TO_DATE(A.YSQJ,'YYYY-MM') <= TO_DATE(#{YSQJ_E},'YYYY-MM')
JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL
