case when CGXYZ22=0 AND CGXYZ19=0 THEN (CGXYZ18-CGXYZ05-CGXYZ20-CGXYZ21) ELSE (CGXYZ04-CGXYZ05-CGXYZ20-CGXYZ21) END KXSSL, A.CGXYZ08 XYSJ,CGXYZ09 XYXJ, A.SPXX01 SPXX01,A.SPXX02 SPBM,A.SPXX04 SPMC, B.CGXY08 BZ From CGXYZX A,CGXY B Where A.CGXY01 = B.CGXY01 AND A.GSXX01 = B.GSXX01 and (CGXYZ04 - CGXYZ05 - CGXYZ20 + CGXYZ21)>0
And A.CGXYZ02 <= TO_DATE(TO_CHAR(Sysdate, 'YYYY-MM-DD'), 'YYYY-MM-DD') And A.CGXYZ03 >= TO_DATE(TO_CHAR(Sysdate, 'YYYY-MM-DD'), 'YYYY-MM-DD') And A.CGXYZ04 <> A.CGXYZ05
And A.GSXX01 = #{GSXX01}
