mapper.select:
SELECT NVL(SUM(B.YSJE),0) YSZE
 FROM FYYSD A,FYYSDITEM B
WHERE A.FYYSD01 = B.FYYSD01
AND A.GSXX01 = B.GSXX01
AND SHRQ IS NOT NULL


	AND A.GSXX01 = #{GSXX01}


	AND B.KMDM = #{CWKM}


	AND B.FYLX01 = #{FYBM}

JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL

SELECT NVL(SUM(QKJE),0) QKZE 
  FROM QKSQD A, QKSQDITEM B
 WHERE A.GSXX01 = B.GSXX01
   AND A.QKSQD01 = B.QKSQD01
   AND A.SHRQ IS NOT NULL
   AND B.YSQJ = TO_CHAR(SYSDATE, 'YYYY-MM')

	AND A.GSXX01 = #{GSXX01}


	AND B.KMDM = #{CWKM}


	AND B.FYLX01 = #{FYBM}

JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL

SELECT A.JLWBDH QKSQD01,A.QKSQD01 DJH01,A.SQRMC,TO_CHAR(A.SQRQ,'YYYY-MM-DD')SQRQ,(SELECT FYLX02 FROM FYLX WHERE FYLX01 = B.FYLX01) FYMC, B.KMDM,B.CUR,B.QKJE,B.YSQJ FROM QKSQD A, QKSQDITEM B WHERE A.GSXX01 = B.GSXX01 AND A.QKSQD01 = B.QKSQD01 AND A.SHRQ IS NOT NULL
AND A.GSXX01 = #{GSXX01}
AND B.FYLX01 = #{FYBM}
AND B.KMDM = #{CWKM}
JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL ORDER BY QKSQD01

SELECT A.JLWBDH QKSQD01,A.BM01,(SELECT BM02 FROM BM WHERE BM01 = A.BM01) BMMC, A.SQRMC,TO_CHAR(A.SQRQ,'YYYY-MM-DD') SQRQ,A.SQYY,A.QKZE,A.ZDRMC, TO_CHAR(A.ZDRQ,'YYYY-MM-DD') ZDRQ,A.SHRMC, TO_CHAR(A.SHRQ,'YYYY-MM-DD') SHRQ,A.CNRMC, TO_CHAR(A.CNRQ,'YYYY-MM-DD') CNRQ, GET_QKSQDZT(A.DJZT) DJZT, B.FYLX01,(SELECT FYLX02 FROM FYLX WHERE FYLX01 = B.FYLX01) FYMC, B.KMDM,B.YSYE,B.CUR, (SELECT WBBZ02 FROM WBBZ WHERE WBBZ01 = B.CUR AND A.GSXX01 = B.GSXX01) WBBZ, B.QKJE,B.SKFS01,(SELECT SKFS02 FROM SKFS WHERE SKFS01 = B.SKFS01) SKFSMC, B.YHZH,B.BZ,B.YSQJ,A.GSXX01 FROM QKSQD A, QKSQDITEM B WHERE A.GSXX01 = B.GSXX01 AND A.QKSQD01 = B.QKSQD01
AND A.JLWBDH = #{QKSQD01}
AND A.DJZT = #{DJZT}
AND A.SQRMC = #{SQRMC}
AND TO_DATE(B.YSQJ,'YYYY-MM') >= TO_DATE(#{YSQJ_S}, 'YYYY-MM')
AND TO_DATE(B.YSQJ,'YYYY-MM') <= TO_DATE(#{YSQJ_E},'YYYY-MM')
JL{CZY|A.GSXX01|CZGS_CZ|LIKE%}JL JL{CZY|A.BM01|BM_CZ|LIKE%}JL
