SELECT R.RYXX01, R.RYXX02 RYMC, R.BM01, B.BM02 BMMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX03 RYZT, GET_RYZT(R.RYXX03) RYZTMC, GET_BOOL(INSTR(R.RYXX08, 0)) RYSX_ALL, GET_BOOL(INSTR(R.RYXX08, 1)) RYSX_GL, GET_BOOL(INSTR(R.RYXX08, 2)) RYSX_YW, GET_BOOL(INSTR(R.RYXX08, 3)) RYSX_XS, GET_BOOL(INSTR(R.RYXX08, 4)) RYSX_SY, GET_BOOL(INSTR(R.RYXX08, 5)) RYSX_CW, GET_BOOL(INSTR(R.RYXX08, 6)) RYSX_BG, GET_BOOL(INSTR(R.RYXX08, 7)) RYSX_QT FROM RYXX R, BM B, GSXX G WHERE R.BM01 = B.BM01 AND R.GSXX01 = G.GSXX01
AND R.GSXX01 = #{GSXX01}
AND R.RYXX01 = #{RYXX01}
AND R.RYXX08 in ${RYXX08}
AND R.BM01 = #{BM01}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

SELECT R.RYXX01, R.RYXX02 RYMC, R.BM01, B.BM02 BMMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX03 RYZT, GET_RYZT(R.RYXX03) RYZTMC,D.FLFS01, GET_BOOL(INSTR(R.RYXX08, 0)) RYSX_ALL, GET_BOOL(INSTR(R.RYXX08, 1)) RYSX_GL, GET_BOOL(INSTR(R.RYXX08, 2)) RYSX_YW, GET_BOOL(INSTR(R.RYXX08, 3)) RYSX_XS, GET_BOOL(INSTR(R.RYXX08, 4)) RYSX_SY, GET_BOOL(INSTR(R.RYXX08, 5)) RYSX_CW, GET_BOOL(INSTR(R.RYXX08, 6)) RYSX_BG, GET_BOOL(INSTR(R.RYXX08, 7)) RYSX_QT, (select FLFS.FLFS01 from QXXSFSITEM,FLFS where QXXSFS01=r.ryxx09 and QXXSFSITEM.FLFS01=FLFS.FLFS01 AND FLFS04 not in ${XSLX_F} AND ROWNUM=1 ) XSFS01, (select FLFS.FLFS02 from QXXSFSITEM,FLFS where QXXSFS01=r.ryxx09 and QXXSFSITEM.FLFS01=FLFS.FLFS01 AND FLFS04 not in ${XSLX_F} AND ROWNUM=1 ) XSFSMC FROM RYXX R, BM B, GSXX G,DWYWY D WHERE R.BM01 = B.BM01 AND R.RYXX03 = 0 AND B.BM10=1 AND R.RYXX09 IS NOT NULL AND R.RYXX01=D.RYXX01 AND R.GSXX01=D.GSXX01
AND INSTR(R.RYXX08, 3) > 0
AND R.GSXX01 = G.GSXX01
AND R.GSXX01 = #{GSXX01}
AND B.GCBJ = -1
AND D.WLDW01 = #{WLDW01}
AND R.RYXX01 = #{RYXX01}
AND R.BM01 = #{BM01}
AND B.BM07 = #{BM07}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
AND exists(select 1 from QXXSFSITEM,FLFS where QXXSFS01=r.ryxx09 and QXXSFSITEM.FLFS01=FLFS.FLFS01 AND flfs04 not in ${XSLX_F})
JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

SELECT R.RYXX01, R.RYXX02 RYMC, CASE WHEN C.BM01 IS NOT NULL THEN C.BM01 ELSE R.BM01 END BM01, CASE WHEN C.BM01 IS NOT NULL THEN (SELECT M.BM02 FROM BM M WHERE M.BM01 = C.BM01) ELSE B.BM02 END BMMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX03 RYZT, GET_RYZT(R.RYXX03) RYZTMC, (SELECT B.FLFS01 FROM QXXSFSITEM A, FLFS B WHERE A.FLFS01 = B.FLFS01 AND B.FLFS04 IN (0, 3) AND A.QXXSFS01 = R.RYXX09 AND B.FLFS14 = 0 AND ROWNUM = 1) XSFS01, (SELECT B.FLFS02 FROM QXXSFSITEM A, FLFS B WHERE A.FLFS01 = B.FLFS01 AND B.FLFS04 IN (0, 3) AND A.QXXSFS01 = R.RYXX09 AND B.FLFS14 = 0 AND ROWNUM = 1) XSFSMC FROM RYXX R, BM B, GSXX G,RYBM C WHERE R.BM01 = B.BM01 AND R.GSXX01 = G.GSXX01 AND R.RYXX01 = C.RYXX01(+) AND R.GSXX01 = SUBSTR(C.BM01(+),0,4) AND B.BM04 = 1 AND B.BM06 = 3 AND R.RYXX03 = 0
AND INSTR(R.RYXX08, 3) > 0
AND R.GSXX01 = #{GSXX01}
AND R.RYXX01 = #{RYXX01}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
AND EXISTS (SELECT B.FLFS01 FROM QXXSFSITEM A, FLFS B WHERE A.FLFS01 = B.FLFS01 AND B.FLFS04 IN (0, 3) AND A.QXXSFS01 = R.RYXX09 AND B.FLFS14 = 0 AND ROWNUM = 1) JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

SELECT R.RYXX01, R.RYXX02 RYMC, R.BM01, B.BM02 BMMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX09 QXXSFS01 FROM RYXX R, BM B, GSXX G WHERE R.BM01 = B.BM01 AND R.RYXX03 = 0 AND INSTR(R.RYXX08, 2) > 0 AND R.GSXX01 = G.GSXX01
AND R.GSXX01 = #{GSXX01}
AND R.RYXX01 = #{RYXX01}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

