SELECT R.RYXX01, R.RYXX02 RYMC, R.BM01, B.BM02 BMMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX03 RYZT, GET_RYZT(R.RYXX03) RYZTMC FROM RYXX R, BM B, GSXX G WHERE R.BM01 = B.BM01 AND R.GSXX01 = G.GSXX01 AND R.RYXX03 = 0
AND R.GSXX01 = #{GSXX01}
AND R.RYXX01 = #{RYXX01}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

SELECT R.RYXX01, R.RYXX02 RYMC, R.GSXX01, G.GSXX02 GSMC, R.RYXX03 RYZT FROM RYXX R, BM B, GSXX G,CZYQXGYS C WHERE R.BM01 = B.BM01 AND R.RYXX03 = 0 AND B.BM10 = 1 AND R.RYXX01=C.QXCZY01 AND C.GYSLX=1 AND R.RYXX09 IS NOT NULL AND INSTR(R.RYXX08, 3) > 0 AND R.GSXX01 = G.GSXX01 AND B.BM01=#{BM01}
AND C.QXCZY01<>#{WLDW01}
AND C.QXGYS01=#{WLDW01} AND R.GSXX01 = #{GSXX01}
AND (R.RYXX02 LIKE '%'||#{RYMC}||'%' OR R.RYXX01 LIKE '%'||#{RYMC}||'%')
JL{CZY|R.BM01|BM_CZ|LIKE%}JL JL{CZY|R.GSXX01|CZGS_CZ|LIKE%}JL

